mapper.select:
SELECT o.order_detail_id, o.order_id, p.order_type, p.order_name, o.adslot_id, w.name adslot_name, o.city_code, a.ka_area city_name, o.scene_id, s.name scene_name, p.ad_channel_id channel_id, c.name channel_name, t.name customer_name, o.estimate_pv, o.estimate_uv, o.reality_pv, o.reality_uv, o.monitor_url, o.put_time FROM pmp_order_detail o LEFT JOIN scene s ON o.scene_id = s.id LEFT JOIN t_area a ON o.city_code = a.ka_id LEFT JOIN pmp_orders p ON o.order_id = p.orderid LEFT JOIN ad_channel c ON p.ad_channel_id = c.id LEFT JOIN ad_customer t ON p.ad_customer_id = t.id LEFT JOIN ad_position w ON o.adslot_id = w.thrid_position_id
1=1
and o.order_id = #{order_id}
and o.adslot_id = #{adslot_id}
and o.city_code = #{city_code}
and o.scene_id = #{scene_id}
and o.put_time >= #{sdate}
and o.put_time <= #{edate}
order by put_time desc
limit #{startSize},#{pageSize}

SELECT count(o.order_detail_id) FROM pmp_order_detail o LEFT JOIN scene s ON o.scene_id = s.id LEFT JOIN t_area a ON o.city_code = a.ka_id LEFT JOIN pmp_orders p ON o.order_id = p.orderid LEFT JOIN ad_channel c ON p.ad_channel_id = c.id LEFT JOIN ad_customer t ON p.ad_customer_id = t.id LEFT JOIN ad_position w ON o.adslot_id = w.thrid_position_id
1=1
and o.order_id = #{order_id}
and o.adslot_id = #{adslot_id}
and o.city_code = #{city_code}
and o.scene_id = #{scene_id}
and o.put_time >= #{sdate}
and o.put_time <= #{edate}
