mapper.select:
		SELECT t.id,t.name,t.ssp_adslot_id,t.city_code,t.scene_id,t.ssp_app_id,t.city_code,t.pv,t.uv,t.device_num from pmp_resource t 
		where
		1=1
		 
		  
       		and t.ssp_adslot_id =#{ssp_adslot_id}
    	  
    	  
       		and t.ssp_app_id = #{ssp_app_id}
    	
    	
    		and t.city_code in
    		
          	     #{item}
        	
    	
    	
    		and t.scene_id in 
    		
          	     #{item}


select sum(t1.num) as stock, t.put_time as put_time
from pmp_order_detail t , 
pmp_orders t1 
where  t.order_id = t1.orderid 
and t1.order_type != '3'
and t.pmp_resouce_stock_id in (
  select t3.id from pmp_resource t2,pmp_resource_stock t3 where t2.id = t3.pmp_resource_id
  and t2.id = #{pmp_resource_id}
)
GROUP BY t.put_time

 	 select t1.id,t1.mdate ,t1.stock from pmp_resource_stock t1 where t1.pmp_resource_id = #{pmp_resource_id} 
 	 
	   and t1.mdate >= #{sdate}
	  
	  
	  	and t1.mdate <= #{edate}

	 		
		 SELECT
		(
			SELECT
				sum(num)
			FROM
				pmp_orders t1,pmp_order_detail t4
			WHERE
				t1.order_type = '1'
			AND t1.orderid = t4.order_id
    		and t1.ad_app_id = #{ad_app_id}
    		and t4.put_time >= #{sdate}
      		and t4.put_time <= #{edate}

		)  AS ydnum,
		(
			SELECT
				sum(num)
			FROM
				pmp_orders t2 ,pmp_order_detail t5
			WHERE
				t2.order_type = '2'
      		AND t2.orderid = t5.order_id
			  and t2.ad_app_id = #{ad_app_id}
       		  and t5.put_time >= #{sdate}
			  and t5.put_time <= #{edate}
			 ) AS sdnum
	FROM
		pmp_orders t
	  WHERE t.ad_app_id = #{ad_app_id}
    GROUP BY t.ad_app_id

mapper.update:
	    update pmp_resource_stock
	     set stock= #{stock}
	    where id in
   		  
       		 #{item}

mapper.select:
		select pmp_resource_id,id as pmp_resource_stock_id,mdate from pmp_resource_stock where 1=1
		
   		and id in
   		
         	     #{item}
