mapper.select:
SELECT t.id,t.name,t.ssp_adslot_id,t4.city_code,t5.scene_id,t2.ssp_app_id,t.pv,t.uv,t.device_num,t10.sailed_cpm as price,t10.cpm,t8.adx_adslot_id from pmp_resource t, ssp_devicegroup t2, ssp_adslot t3,pmp_resource_city t4, pmp_resource_scene t5,ssp_app t6,adx_app_devicegroup t8,adx_app t9,pmp_adxadslot_relation t10 where t.ssp_adslot_id = t3.id and t2.id = t3.ssp_devicegroup_id and t3.id = t.ssp_adslot_id and t2.ssp_app_id = t6.id and t4.pmp_resource_id = t.id and t5.pmp_resource_id = t.id
and t8.adx_app_id = t9.id and t.id = t10.pmp_resource_id and t10.adx_adslot_id = t8.adx_adslot_id
and t9.id = #{adx_app_id}
and t4.city_code in
#{item}
and t5.scene_id in
#{item}
and t9.channel_id = #{channel_id}

		  select t.id,t.name,t.ssp_adslot_id,t1.city_code,t2.scene_id,t3.ssp_app_id,t.pv,t.uv,t.device_num,t.price,t.cpm
		  from pmp_resource t, pmp_resource_city t1,pmp_resource_scene t2,ssp_devicegroup t3,ssp_adslot t4,ssp_app t5
		  where t.id = t1.pmp_resource_id 
		  and t.id = t2.pmp_resource_id
		  and t3.id = t4.ssp_devicegroup_id
		  and t4.id = t.ssp_adslot_id
		  and t3.ssp_app_id = t5.id
		    
       		and t3.ssp_app_id = #{ssp_app_id}
    	 
    	 
    		and t1.city_code in
    		
          	     #{item}
        	
    	
    	
    		and t2.scene_id in 
    		
          	     #{item}
        	
    	
    	   
       		and t.ssp_adslot_id = #{ssp_adslot_id}
    	 
		   
       		and t5.media_id = #{media_id}


select sum(t1.num) as stock, t.put_time as put_time
from pmp_order_detail t , 
pmp_orders t1 
where  t.order_id = t1.orderid 
and t1.order_type != '3'
and t.pmp_resouce_stock_id in (
  select t3.id from pmp_resource t2,pmp_resource_stock t3 where t2.id = t3.pmp_resource_id
  and t2.id = #{pmp_resource_id}
)
GROUP BY t.put_time

 	 select t1.id,t1.mdate ,t1.stock from pmp_resource_stock t1 where t1.pmp_resource_id = #{pmp_resource_id} 
 	 
	   and t1.mdate >= #{sdate}
	  
	  
	  	and t1.mdate <= #{edate}

select t1.ssp_app_id as app_id ,sum(t3.stock) total from pmp_resource t,ssp_devicegroup t1, ssp_adslot t2,pmp_resource_stock t3,ssp_app t4 where t.ssp_adslot_id = t2.id and t2.ssp_devicegroup_id = t1.id and t.id = t3.pmp_resource_id and t1.ssp_app_id = t4.id
and t4.media_id = #{media_id}
GROUP BY t1.ssp_app_id

mapper.update:
	    update pmp_resource_stock
	     set stock= #{stock}
